INSERT INTO "public"."homepage_faq_faqs" ("_order", "_parent_id", "id", "question", "answer", "_locale") VALUES
(1, 1, '68d950f5e9d5d9354ea8eefb', 'Was ist eine Forex Prop Firm?', 'Eine Forex prop firm ermöglicht es Tradern, mit größerem virtuellem Kapital zu handeln, ohne ihr eigenes Geld zu riskieren. Trader können je nach ihrer Fähigkeit, konstante Gewinne zu erzielen, echte Auszahlungen erhalten. Bei Funded Trader Markets (FTM) können Sie Ihre Fähigkeiten entweder durch den Evaluierungsprozess oder direkt mit einem Instant Funded Account unter Beweis stellen und den Großteil Ihrer Gewinne behalten.', 'de'),
(2, 1, '68d950f5e9d5d9354ea8eefc', 'Was ist ein Forex Funded Account?', 'Ein Forex Funded Account ist ein Konto, das von einer prop firm bereitgestellt wird. Anstatt Ihr eigenes Kapital zu verwenden, handeln Sie mit größerem virtuellem Kapital. So können talentierte Trader ohne hohes finanzielles Risiko profitieren.', 'de'),
(3, 1, '68d950f5e9d5d9354ea8eefd', 'Wie qualifiziert man sich für ein simuliertes finanziertes Konto?', 'Bei FTM haben Sie in der Regel zwei Optionen, um ein simuliertes finanziertes Konto zu erhalten:
INSERT INTO "public"."homepage_payouts_features_features" ("_order", "_parent_id", "id", "title", "description", "position", "_locale") VALUES
(1, 1, '68d950f5e9d5d9354ea8eef3', 'Großzügige Gewinnbeteiligung', 'Behalten Sie bis zu 100 % Ihrer Gewinne in den Evaluierungsprogrammen und bis zu 80 % mit Instant-Finanzierung.', 'left-tall', 'de'),
(2, 1, '68d950f5e9d5d9354ea8eef4', 'Auszahlung auf Abruf', 'Haben Sie die Kriterien erfüllt? Fordern Sie sofort Ihre Auszahlung an. Keine 30-, 14- oder 7-Tage-Zyklen. Hunderte von geprüften Auszahlungen finden Sie in unserem Discord und im Payout Junction.', 'top-right', 'de'),
(3, 1, '68d950f5e9d5d9354ea8eef5', '24h oder doppelte Auszahlung', 'Wir garantieren Ihre Auszahlung innerhalb von 24 Stunden. Falls wir uns verspäten, verdoppeln wir Ihren Gewinn und geben Ihnen ein kostenloses Konto. Volles Vertrauen, null Ausreden.', 'bottom-middle', 'de'),
(4, 1, '68d950f5e9d5d9354ea8eef6', 'Durchschnittliche Bearbeitungszeit', 'Die meisten Auszahlungen werden in weniger als 2 Stunden bearbeitet. Wir sind deutlich schneller als der Branchendurchschnitt – mit 24h-Garantie.', 'right-tall', 'de'),
(1, 1, '68d9d2413e77ad100dac9205', 'Cömert Kâr Payı', 'Değerlendirme programlarımızda kârınızın %100’üne kadarını, anında fonlama hesaplarımızda ise %80’ine kadarını siz alın.', 'left-tall', 'tr'),
(2, 1, '68d9d2413e77ad100dac9206', 'Talebe Bağlı Ödeme', 'Kriterler sağlandı mı? Anında çekim yapın. 30, 14 veya 7 günlük döngüler yok. Discord ve payout junction’da yüzlerce kanıtlı yorum!', 'top-right', 'tr'),
